
document.addEventListener('DOMContentLoaded', (function () {

  // страница О КОМПАНИИ
  if (document.querySelector('.about-company-slider')) {

    let aboutCompanySliderText = document.querySelectorAll('.js_about_company_slider_text');//текстовый блок
    let aboutCompanySliderTextWrap = document.querySelectorAll('.about-company-slider__slide-text-wrapper');//блок с текстом

    // SWIPER ONE-PAGE-SCROLL на странице О КОМПАНИИ
      var aboutCompanyOnePageSlider = new Swiper('.about-company-slider', {
        // Optional parameters
        direction: 'vertical',
        slidesPerView: 1,
        mousewheel: true,
        speed: 700,
      
        // If we need pagination
        pagination: {
          el: '.swiper-pagination',
          clickable: true,
        },
      
        // Navigation arrows
        navigation: {
          nextEl: '.swiper-button-next',
          prevEl: '.swiper-button-prev',
        },
      
        // And if we need scrollbar
        scrollbar: {
          el: '.swiper-scrollbar',
        },
      })
    
      aboutCompanySliderText.forEach((elem,index) => {

        elem.addEventListener('mouseover', () => {
          aboutCompanyOnePageSlider.mousewheel.disable();
            console.log('1');
          // if(elem.clientHeight < aboutCompanySliderTextWrap[index].clientHeight) {
          // }          
        });

        elem.addEventListener('mouseout', () => {
          aboutCompanyOnePageSlider.mousewheel.enable();
        });
      });
 

    let aboutCompanySliderSlide = document.querySelectorAll('.about-company-slider__slide');

    let slideOne = aboutCompanySliderSlide[0];//слайд #1
    let slideTwo = aboutCompanySliderSlide[1];//слайд #2
    let slideThree = aboutCompanySliderSlide[2];//слайд #3
    let slideFour = aboutCompanySliderSlide[3];//слайд #4

    let observerOne = new MutationObserver(mutationRecords => {

      if (slideOne.classList.contains('swiper-slide-active')) {

        addAnmateSlide(slideOne)

      } else {
        removeAnmateSlide(slideOne)
      }
    });

    let config = { attributes:true };
    observerOne.observe(slideOne, config);

    let observerTwo = new MutationObserver(mutationRecords => {

      if (slideTwo.classList.contains('swiper-slide-active')) {

        addAnmateSlide(slideTwo)

      } else {
        removeAnmateSlide(slideTwo)
      }

    });
    observerTwo.observe(slideTwo, config);

    let observerThree = new MutationObserver(mutationRecords => {
      if (slideThree.classList.contains('swiper-slide-active')) {
        addAnmateSlide(slideThree)
      } else {
        removeAnmateSlide(slideThree)
      }
    });
    observerThree.observe(slideThree, config);

    let observerFour = new MutationObserver(mutationRecords => {
      if (slideFour.classList.contains('swiper-slide-active')) {
        addAnmateSlide(slideFour)
      } else {
        removeAnmateSlide(slideFour)
      }
    });
    observerFour.observe(slideFour, config);




    
    function addAnmateSlide(slide) {
      let aboutText = slide.querySelector('.about-company-slider__slide-text');
      let aboutTitle = slide.querySelector('.about-company-slider__slide-title');
      let aboutBg = slide.querySelector('.about-company-slider__slide-bg');

      aboutText.classList.add('animtion-text');
      aboutTitle.classList.add('animation-about-title');
      aboutBg.classList.add('animation-about-bg');

    }
    function removeAnmateSlide(slide) {
      let aboutText = slide.querySelector('.about-company-slider__slide-text');
      let aboutTitle = slide.querySelector('.about-company-slider__slide-title');
      let aboutBg = slide.querySelector('.about-company-slider__slide-bg');

      aboutText.classList.remove('animtion-text');
      aboutTitle.classList.remove('animation-about-title');
      aboutBg.classList.remove('animation-about-bg');

    }
      
      


  }

})());


