'use strict';

(function () {
  var gallerySliderEl = document.querySelector('.gallery__slider');
  var gallerySwiper;

  function getStretch() {
    var windowWidth = window.innerWidth;
    return (0.439 * windowWidth);
  }

  function getDeph() {
    var windowWidth = window.innerWidth;
    return (0.134 * windowWidth);
  }


  var initGallerySlider = function () {
    gallerySwiper = new window.Swiper(gallerySliderEl, {
      speed: 300,
      slidesPerView: 1,
      loop: false,
      navigation: {
        nextEl: '.gallery .slider-button--next',
        prevEl: '.gallery .slider-button--prev',
        disabledClass: 'slider-button--disabled',
      },
      effect: 'coverflow',
      coverflowEffect: {
        rotate: 0,
        stretch: 835,
        depth: 255,
        modifier: 1,
        slideShadows: false,
      },
      breakpoints: {
        768: {
          slidesPerView: 1,
          coverflowEffect: {
            stretch: 0,
            depth: 0,
            modifier: 1,
            slideShadows: false,
          }
        },
        1024: {
          coverflowEffect: {
            rotate: 0,
            stretch: getStretch(),
            depth: getDeph(),
            modifier: 1,
            slideShadows: false,
          },
        },
      }
    });

    return gallerySwiper;
  };

  var resizeTimer;

  if (gallerySliderEl) {

    if (window.matchMedia('(min-width: 768px)').matches === true) {
      gallerySwiper = initGallerySlider();
    }

    window.addEventListener('resize', function () {
      clearTimeout(resizeTimer);
      resizeTimer = setTimeout(function () {
        if (window.matchMedia('(min-width: 768px)').matches === true) {
          gallerySwiper = initGallerySlider();
        } else if (gallerySwiper) {
          gallerySwiper.destroy(true, true);
        }
      }, 250);
    });
  }

})();

