(function () {
  var newsTabs = document.querySelector('.news__tabs');
  var newsContainer = document.querySelector('.news__container');

  if (!newsTabs) {
    return;
  }
  var headerLinks = newsTabs.querySelectorAll('.news__header-link');
  var newsLists = newsContainer.querySelectorAll('.news__news-list');

  headerLinks[0].addEventListener('click', function () {
    newsLists[1].classList.add('visually-hidden');
    newsLists[0].classList.remove('visually-hidden');

    headerLinks[0].classList.add('news__header-link--current');
    headerLinks[1].classList.remove('news__header-link--current');

    headerLinks[0].classList.remove('news__header-link--active');
    headerLinks[1].classList.add('news__header-link--active');
  });

  headerLinks[1].addEventListener('click', function () {
    newsLists[0].classList.add('visually-hidden');
    newsLists[1].classList.remove('visually-hidden');

    headerLinks[0].classList.remove('news__header-link--current');
    headerLinks[1].classList.add('news__header-link--current');

    headerLinks[0].classList.add('news__header-link--active');
    headerLinks[1].classList.remove('news__header-link--active');
  });

  // slider
  var newsList = document.querySelector('.news__news-news');
  var papersList = document.querySelector('.news__news-papers');
  var swiperNews = null;
  var swiperPapers = null;

  var createSliderNews = function () {
    swiperNews = new window.Swiper(newsList, {
      speed: 600,
      allowTouchMove: true,
      loop: true,
      slidesPerView: 'auto',
      slidesPerColumn: 1,
      spaceBetween: 0,
    });

    return swiperNews;
  };

  var createSliderPapers = function () {
    swiperPapers = new window.Swiper(papersList, {
      speed: 600,
      allowTouchMove: true,
      loop: true,
      slidesPerView: 'auto',
      slidesPerColumn: 1,
      spaceBetween: 0,
    });

    return swiperPapers;
  };

  var initSliderNews = function () {
    if (document.body.clientWidth <= 1023) {
      if (!swiperNews) {
        createSliderNews();
      }
    } else {
      if (swiperNews) {
        swiperNews.destroy();
        swiperNews = null;
      }
    }
  };

  var initSliderPapers = function () {
    if (document.body.clientWidth <= 1023) {
      if (!swiperPapers) {
        createSliderPapers();
      }
    } else {
      if (swiperPapers) {
        swiperPapers.destroy();
        swiperPapers = null;
      }
    }
  };

  var onWindowResizeNews = function () {
    initSliderNews();
    initSliderPapers();
  };

  initSliderNews();
  initSliderPapers();
  window.addEventListener('resize', onWindowResizeNews);
})();

