'use strict';

(function () {
  var chooseOtherForm = document.querySelector('.choose-other__form');
  var isMain = document.querySelector('.starlink-main');

  if (!chooseOtherForm || !isMain) {
    return;
  }

  // select
  var selectBox = chooseOtherForm.querySelector('.select-modification');
  var selectBoxShort = chooseOtherForm.querySelector('.select-modification--short');
  var selectOptions = selectBox.querySelector('.swiper-wrapper');
  var modificationInput = chooseOtherForm.querySelector('.choose-other__modification-input');
  var isHidden = false;

  var openOptions = function (evt) {
    var target = evt.target;
    var isNode = target.parentNode.classList.contains('select-modification') || target.parentNode.parentNode.classList.contains('select-modification');

    if (isHidden) {
      selectBox.dataset.hidden = true;
      isHidden = false;
    } else if (isNode) {
      isHidden = true;
      selectBox.dataset.hidden = false;
      selectBox.dataset.selected = false;
    }
  };

  var checkedOption = function (evt) {
    var target = evt.target;
    var isElement = target.classList.contains('select-modification__item') || target.parentNode.classList.contains('select-modification__item');

    if (isElement) {
      var selectValue = selectBox.querySelector('.select-modification__value');
      var selectText = selectValue.querySelector('span');

      selectBox.dataset.hidden = true;
      selectBox.dataset.selected = true;
      selectText.textContent = target.textContent;
      selectValue.dataset.value = (target.dataset.value || target.parentNode.dataset.value);
      modificationInput.value = (target.dataset.value || target.parentNode.dataset.value);

      activeOption();
    }

    function activeOption() {
      var selectItems = selectBox.querySelectorAll('.select-modification__item');

      selectItems.forEach(function (item) {
        item.removeAttribute('data-active');
      });


      if (target.classList.contains('select-modification__item')) {
        target.dataset.active = true;
      } else if (target.parentNode.classList.contains('select-modification__item')) {
        target.parentNode.dataset.active = true;
      }
    }
  };

  var onClickSelectBox = function (evt) {
    openOptions(evt);
  };

  var onClickSelectOptions = function (evt) {
    checkedOption(evt);
  };

  var initSelect = function () {
    if (window.matchMedia('(max-width: 1023px)').matches) {
      selectBox.removeEventListener('click', onClickSelectBox);
      selectOptions.removeEventListener('click', onClickSelectOptions);
      selectBox.addEventListener('click', onClickSelectBox);
      selectOptions.addEventListener('click', onClickSelectOptions);
    } else if (window.matchMedia('(min-width: 1023px)').matches) {
      selectBox.removeEventListener('click', onClickSelectBox);
      selectOptions.removeEventListener('click', onClickSelectOptions);
      selectOptions.addEventListener('click', onClickSelectOptions);
    }
  };

  // slider

  var pagination = chooseOtherForm.querySelectorAll('.select-modification__pagination');
  var swiper = null;

  var createSlider = function () {
    var isOpen = selectBox.dataset.hidden;

    if (isOpen === 'false') {
      selectBox.dataset.hidden = true;
    }

    swiper = new window.Swiper(selectBox, {
      slidesPerView: 1,
      speed: 600,
      spaceBetween: 15,
      pagination: {
        el: pagination[0],
        clickable: true,
      },

    });

    return swiper;
  };

  var swiperShort = new window.Swiper(selectBoxShort, {
    slidesPerView: 1,
    speed: 600,
    spaceBetween: 15,
    pagination: {
      el: pagination[1],
      clickable: true,
    },
  });

  var initSlider = function () {
    if (window.matchMedia('(min-width: 1024px)').matches) {
      if (!swiper) {
        createSlider();
      }
    } else {
      if (swiper) {
        swiper.destroy(true, true);
        swiper = null;
      }
    }
  };

  var onWindowResize = function () {
    initSlider();
    initSelect();
  };

  if (window.matchMedia('(min-width: 1024px)').matches) {
    initSlider();
  } else {
    initSelect();
  }

  window.addEventListener('resize', onWindowResize);
})();

