'use strict';

(function () {
  var body = document.querySelector('body');
  var menuButtonClass = 'menu-btn';
  var menuButtonClassActive = menuButtonClass + '--active';
  var menuButton = document.querySelector('.' + menuButtonClass);
  var menuClass = 'site-nav';
  var menuClassOpened = menuClass + '--open';
  var menu = document.querySelector('.' + menuClass);
  var searchButton = document.querySelector('.header__search-link');//иконка поиска
  let headerSearch = document.querySelector('.header__search.search-input');//блок поиска
  let headerSearchInput = document.querySelector('.header__search-input.search');//input поиска
  let headerSearchButton = document.querySelector('.header__search-button.search');//кнопка поиска в строке поиска

  var closeMenu = function () {
    menuButton.classList.remove(menuButtonClassActive);
    menu.style.opacity = '0';
    body.classList.remove('menu-opened');
  };

  //при клике на body закрывает поиск
  body.addEventListener('click', () => {
   // console.log('body');
    
    if(headerSearch.classList.contains('header__search--opened')) {
      headerSearch.classList.remove('header__search--opened');
  //console.log('close');
    }
  //else console.log(headerSearch.classList);

  });

  //отменяет событие которе у body
  searchButton.addEventListener('click', (e) => {
    e.stopPropagation();
  });

  //отменяет событие которе у body
  headerSearch.addEventListener('click', (e) => {
    e.stopPropagation();
  });

  //проверка введеного пользователем текста
  headerSearchButton.addEventListener('click', () => {
    console.log(headerSearchInput.value);
    if(headerSearchInput.value.length < 3) {

      headerSearchInput.value = "введите больше 3-х символов";
      headerSearchInput.addEventListener('focus', () => {
        headerSearchInput.value = "";
      });
    } else {
      headerSearchInput.value = "";
      document.location.href = "desktop-search.html";
    }
  });


  var toggleMenu = function () {
    if (!body.classList.contains('menu-opened')) {
      menuButton.classList.add(menuButtonClassActive);
      menu.style = '';
      menu.classList.add(menuClassOpened);
      body.classList.add('menu-opened');
    } else {
      closeMenu();
    }
  };

  // ф-ия закрытие/открытия строки поиска
  var toggleSearch = function (evt) {
    var headerSearchElement = searchButton.parentNode;
    headerSearchElement.classList.toggle('header__search--opened');
  };

  menuButton.addEventListener('click', toggleMenu);
  searchButton.addEventListener('click', toggleSearch);

  window.addEventListener('keydown', function (evt) {
    if ((evt.keyCode === 27) && (body.classList.contains('menu-opened'))) {
      closeMenu();
    }
  });

  menu.addEventListener('transitionend', function () {
    if (menu.style.opacity === '0') {
      menu.classList.remove(menuClassOpened);
    }
  });

  // раскрытие списка в меню------------
  var expandButtonClass = 'site-nav__expand-btn';
  var expandButtonClassOpened = expandButtonClass + '--opened';
  var sublistClass = 'site-nav__expand-btn';
  var sublistClassOpened = sublistClass + '--opened';
  var expandButton = document.querySelector('.' + expandButtonClass);
  var expandLink = document.querySelector('.site-nav__link--expand');
  var sublist = document.querySelector('.' + sublistClass);

  var expandList = function (evt) {
    evt.preventDefault();
    var expandingElement = expandButton.parentNode.nextElementSibling;
    expandButton.classList.toggle(expandButtonClassOpened);

    if (!expandingElement.classList.contains(sublistClassOpened)) {
      expandingElement.style.height = expandingElement.scrollHeight + 'px';
      expandingElement.style.overflow = 'hidden';
      expandingElement.classList.add(sublistClassOpened);
    } else {
      expandingElement.style.height = expandingElement.scrollHeight + 'px';
      window.getComputedStyle(expandButton, null).getPropertyValue('height');
      expandingElement.style = '';
      expandingElement.classList.remove(sublistClassOpened);
    }
  };

  expandLink.addEventListener('click', expandList);

  sublist.addEventListener('transitionend', function (evt) {
    var curTarget = evt.currentTarget;

    if (sublist.classList.contains(sublistClassOpened)) {
      curTarget.style = '';
    }
  });

  // списки меню открыть/закрыть-----
  let siteNavOpenBtn = document.querySelectorAll('.menu-open-dropdown');//кнопка открытия
  let menuListHide = document.querySelectorAll('.menu-list-hide');

  if (siteNavOpenBtn) {

    siteNavOpenBtn.forEach((elem, index) => {
      elem.addEventListener('click', () => {
        // console.log(menuListHide[index]);

        menuListHide[index].classList.toggle('menu-list-show');
      });
    })

  }

})();

'use strict';

(function () {
  var activePanelClass = 'tabs__panel--active';
  var activeTabClass = 'tabs__tab--active';
  var tabs = document.querySelectorAll('.tabs');

  if (tabs.length <= 0) {
    return;
  }

  var tabsLinkMore = tabs[0].querySelector('.tabs__link-more');

  var toggleActiveClasses = function (el, activeClassName, parentElement) {
    if (el.classList.contains(activeClassName)) {
      return;
    }

    var activeEl = parentElement.querySelector('.' + activeClassName);
    if (activeEl) {
      activeEl.classList.remove(activeClassName);
    }
    el.classList.add(activeClassName);
  };

  tabs.forEach(function (el) {
    el.addEventListener('click', function (evt) {
      evt.preventDefault();
      var target = evt.target;

      if (target.classList.contains('tabs__tab')) {
        var panelId = target.getAttribute('href');
        var panel = el.querySelector(panelId);

        toggleActiveClasses(panel, activePanelClass, el);
        toggleActiveClasses(target, activeTabClass, el);

        if (el.querySelector('#media-panel').classList.contains(activePanelClass)) {
          tabsLinkMore.classList.remove('tabs__link-more--display-none');
        } else {
          tabsLinkMore.classList.add('tabs__link-more--display-none');
        }
      }
    });
  });

})();

