'use strict';

(function () {
  var chooseOtherForm = document.querySelector('.choose-other__dropdown');

  if (!chooseOtherForm) {
    return;
  }

  var selectBox = chooseOtherForm.querySelector('.select-scope-application');
  var selectOptions = selectBox.querySelector('.select-scope-application__options');
  var scopeApplicationInput = chooseOtherForm.querySelector('.choose-other__scope-application-input');
  var isHidden = false;

  var openOptions = function (evt) {
    var target = evt.target;
    var isNode = target.parentNode.classList.contains('select-scope-application') || target.parentNode.parentNode.classList.contains('select-scope-application');

    if (isHidden) {
      selectBox.dataset.hidden = true;
      isHidden = false;
    } else if (isNode) {
      isHidden = true;
      selectBox.dataset.hidden = false;
      selectBox.dataset.selected = false;
    }
  };

  var checkedOption = function (evt) {
    var target = evt.target;
    var isElement = target.classList.contains('select-scope-application__item') || target.parentNode.classList.contains('select-scope-application__item');

    if (isElement) {
      var selectValue = selectBox.querySelector('.select-scope-application__value');
      var selectText = selectValue.querySelector('span');

      selectBox.dataset.hidden = true;
      selectBox.dataset.selected = true;
      selectValue.classList.add('value__selected');
      selectText.textContent = target.textContent;
      selectValue.dataset.value = (target.dataset.value || target.parentNode.dataset.value);
      scopeApplicationInput.value = (target.dataset.value || target.parentNode.dataset.value);

      activeOption();
    }

    function activeOption() {
      var selectItems = selectBox.querySelectorAll('.select-scope-application__item');

      selectItems.forEach(function (item) {
        item.removeAttribute('data-active');
      });


      if (target.classList.contains('select-scope-application__item')) {
        target.dataset.active = true;
      } else if (target.parentNode.classList.contains('select-scope-application__item')) {
        target.parentNode.dataset.active = true;
      }
    }
  };

  var onClickSelectBox = function (evt) {
    openOptions(evt);
  };

  var onClickSelectOptions = function (evt) {
    checkedOption(evt);
  };

  selectBox.addEventListener('click', onClickSelectBox);
  selectOptions.addEventListener('click', onClickSelectOptions);

})();





