(function() {
  var search = document.querySelector('.search');

  if (!search) {
    return;
  }

  var searchSwiper = document.querySelector('.search-paginator');

  // toggle logic
  var catalogBtn = document.querySelector('.search__sort-block_catalog');
  var articlesBtn = document.querySelector('.search__sort-block_articles');

  var articleSlide = document.querySelectorAll('.article-slide');
  var catalogSlide = document.querySelectorAll('.catalog-slide');

  var settings = {
    slidesPerView: 'auto',
    // spaceBetween: 20,
    touchRatio: 0,
    speed: 0,
    slideToClickedSlide: true,
    preventClicksPropagation: false,
    preventClicks: false,
    observer: true,
    observeParents: true,
    observeSlideChildren: true,
    pagination: {
      el: '.desktop-news-slider__pagination',
      clickable: true,
    },
  };

  var initSearchPaginator = function() {
    var swiper = new Swiper(searchSwiper, settings);


if (articlesBtn) {
    articlesBtn.addEventListener('click', function() {
      // swiper.destroy(true, true);

      articlesBtn.classList.remove('search__sort-block_active-btn');
      catalogBtn.classList.add('search__sort-block_active-btn');

      catalogSlide.forEach(function(e) {
        e.classList.add('hidden');
      });
      articleSlide.forEach(function(e) {
        e.classList.remove('hidden');
      });
      // swiper.update(true);
      // swiper.pagination.update();
    });
  }
if (catalogBtn){
    catalogBtn.addEventListener('click', function() {
      articlesBtn.classList.add('search__sort-block_active-btn');
      catalogBtn.classList.remove('search__sort-block_active-btn');

      catalogSlide.forEach(function(e) {
        e.classList.remove('hidden');
      });
      articleSlide.forEach(function(e) {
        e.classList.add('hidden');
      });
    });
  }
    
  };

  initSearchPaginator();

  // paginator logic
  var searchBullet = document.querySelector('.desktop-news-slider__pagination');
  if (searchBullet) var bullet = searchBullet.querySelectorAll('.swiper-pagination-bullet');
  var pageNum = null;

if (searchBullet) {
  bullet.forEach(function(e, i) {
    e.addEventListener('click', function(id) {
      pageNum = i + 1;
      changeHash(id);
      window.scrollTo(0, 0);
    });
  });
}

  function changeHash(id) {
    id = pageNum;
    try {
      history.replaceState(null, null, '?pageId=' + id);
    } catch (e) {
      location.hash = '#pageId_' + id;
    }
  }
})();

