document.addEventListener('DOMContentLoaded', (function () {

  var elem = document.querySelectorAll('.diametr');

  elem.forEach(function (e) {
    e.addEventListener('click', function (event) {

      toggle();

      if (event.target.closest('LI')) {
        setValue(event.target.innerHTML, event.target.dataset.value);
        close();
      } else if (event.target.closest('button').classList.contains('reset--diametr')) {
        resetValue();
        document.querySelectorAll('.reset--diametr').forEach(reset);
      }
    });
  });

  function reset(item) {
    item.classList.add('hidden');
    close();
  }

  document.querySelectorAll('.catalog__filter_btn-reset').forEach(function (b) {
    b.addEventListener('click', function () {
      resetValue();
      document.querySelectorAll('.reset--diametr').forEach(function (el) {
        el.classList.add('hidden');
      });
    });
  });

  var isOpen = false;

  function onDocumentClick(event) {
    if (!elem[0].contains(event.target) && !elem[1].contains(event.target)) {
      close();
    }
  }

  function setValue(title, value) {
    elem.forEach(function (e) {
      e.querySelector('.dropdown-diametr_output').innerHTML = title;
    });

    var widgetEvent = new CustomEvent('selectDiametr', {
      bubbles: true,
      detail: {
        title: title,
        value: value,
      },
    });

    elem[0].dispatchEvent(widgetEvent);

    showReset();
    function showReset() {
      if (widgetEvent.detail.value !== null) {
        document.querySelectorAll('.reset--diametr').forEach(function (e) {
          e.classList.remove('hidden');
        });
      } else {
        document.querySelectorAll('.reset--diametr').forEach(function (e) {
          e.classList.add('hidden');
        });
      }
    }
  }

  function resetValue() {
    elem.forEach(function (e) {
      e.querySelector('.dropdown-diametr_output').innerHTML = 'Любой';
    });

    var widgetEvent = new CustomEvent('selectDiametr', {
      bubbles: true,
      detail: {
        title: null,
        value: null,
      },
    });
    elem[0].dispatchEvent(widgetEvent);
  }

  function toggle() {
    if (isOpen) {
      close();
    } else {
      open();
    }
  }

  function open() {
    document.querySelectorAll('.arrow__down--diametr').forEach(function (e) {
      e.classList.add('hidden');
    });

    document.querySelectorAll('.arrow__up--diametr').forEach(function (e) {
      e.classList.remove('hidden');
    });

    elem.forEach(function (e) {
      e.classList.add('open');
    });

    document.addEventListener('click', onDocumentClick);
    isOpen = true;
  }

  function close() {
    document.querySelectorAll('.arrow__down--diametr').forEach(function (e) {
      e.classList.remove('hidden');
    });
    document.querySelectorAll('.arrow__up--diametr').forEach(function (e) {
      e.classList.add('hidden');
    });

    elem.forEach(function (e) {
      e.classList.remove('open');
    });

    document.removeEventListener('click', onDocumentClick);
    isOpen = false;
  }


})());

